<?php/* @var $this yii\web\View */use backend\assets\LoginAsset;use yii\helpers\Html;use yii\bootstrap\ActiveForm;LoginAsset::register($this);$this->beginPage();$this->title = 'Admin后台管理';?><!DOCTYPE html><html lang="<?php echo Yii::$app->language?>"><head>	<meta charset="<?php echo Yii::$app->charset?>">	<meta name="viewport" content="width=device-width,initial-scale=1">	<?php echo Html::csrfMetaTags();?>	<title><?php echo Html::encode($this->title)?></title>	<?php $this->head()?></head><body><?php $this->beginBody()?>	<div class="container-fluid">		<div class="row">			<div class="col-md-offset-3 col-md-6">				<div class="container-fluid">					<div class="row">						<h1 class="page-header">登录</h1>					</div>					<div class="row">						<?php $form = ActiveForm::begin(['enableClientValidation'=>true,'options'=>['class'=>'form-horizontal'],'successCssClass'=>'',]); ?>						<?php							$fieldConfig = [								'template'		=>"{label}\n<div class='col-md-5'>{input}</div>\n{hint}\n{error}",								'labelOptions'	=>['class'=>'col-md-2 control-label'],								'errorOptions'	=>['tag'=>'div','class'=>'col-md-5 help-block help-block-error'],							];							//user_name							echo $form->field($model, 'username',$fieldConfig)										->textInput(['placeholder'=>$model->getAttributeLabel('username')]);							//password							echo $form->field($model, 'password',$fieldConfig)								->passwordInput(['placeholder'=>$model->getAttributeLabel('password')]);						?>						<div class="form-group">							<div class="col-sm-offset-3 col-sm-9">								<?= Html::submitButton('登录2',['class'=>'btn btn-primary'])?>							</div>						</div>						<?php ActiveForm::end();?>					</div>				</div>			</div>		</div>	</div><?php $this->endBody();?></body></html><?php $this->endPage();?>